import React from "react";
import { Container, InputGroup, Form, Card, Button } from "react-bootstrap";
import { useState } from "react";

function ToDoList() {
  const [toDoList, setToDoList] = useState([
    {
      name: "Karthik",
      title: "ReactJS",
      description: "Learn props and state",
      date: "05-10-2022",
    },
  ]);
    
    
  const current = new Date();
    const date = `${current.getDate()}/${current.getMonth() + 1}/${current.getFullYear()}`;
    

  return (
    <Container>
      {/* --------------NAME-------------------------------------------------------------------------- */}
      <InputGroup className="mb-3">
        <Form.Control
          placeholder="Name"
          aria-describedby="inputGroup-sizing-default"
        />
      </InputGroup>

      {/* -------------------TITLE--------------------------------------------------------------------------- */}
      <InputGroup className="mb-3">
        <Form.Control
          placeholder="Title"
          aria-describedby="inputGroup-sizing-default"
        />
      </InputGroup>

      {/* ------------------------DESCRIPTION------------------------------------------------------------------------------- */}
      <InputGroup className="mb-3">
        <Form.Control
          placeholder="Description"
          aria-describedby="inputGroup-sizing-default"
        />
      </InputGroup>

      {/* -----------------------------DATE---------------------------------------------------------------------------------------- */}
      <InputGroup className="mb-3">
        <Form.Control
          aria-label="Text input with dropdown button"
          type="date"
        />
      </InputGroup>

      {/* ---------------------------CARD---------------------------------------------------------------------------------------- */}

    {toDoList.map((toDo, index) => (
      <Card>
        <Card.Header as="h5">{toDo.name}</Card.Header>
        <Card.Body>
          <Card.Title>{toDoList.title}</Card.Title>
          <Card.Text>{toDoList.description}</Card.Text>
          <Card.Text>{date}</Card.Text>
          <Card.Text>{toDoList.date}</Card.Text>
          <Button variant="primary">Delete</Button>
        </Card.Body>
        </Card>
        ))}
    </Container>
  );
}

export default ToDoList;
