import React from "react";
import { Container, InputGroup, Form, Card, Button } from "react-bootstrap";
import { useState } from "react";
import './ToDoList.css'
function ToDoList() {
  const [toDoList, setToDoList] = useState([
    {
      name: "Karthik",
      title: "ReactJS",
      description: "Learn props and state",
      date: "05-10-2022",
    },
    {
      name: "Karthik",
      title: "ReactJS",
      description: "Learn props and state",
      date: "05-10-2022",
    },
  ]);

    const [inputName, setInputName] = useState("");
    const [inputTitle, setInputTitle] = useState("");
    const [inputDescription, setInputDescription] = useState("");
    const [inputDate, setInputDate] = useState("");
  const current = new Date();
  const date = `${current.getDate()}/${
    current.getMonth() + 1
  }/${current.getFullYear()}`;

    
    const addInput = () => {
        setToDoList([...toDoList, {
            name: 'inputName',
            title: 'inputTitle',
            description: 'inputDescription',
            
        }])
    }
  return (
      <Container>
          <h1>To-Do-List</h1>
      {/* --------------NAME-------------------------------------------------------------------------- */}
      <InputGroup className="mb-3">
        <Form.Control
          placeholder="Name"
                  aria-describedby="inputGroup-sizing-default"
                  value={inputName}
                  onChange = {(e) => setInputName(e.target.value)}

        />
      </InputGroup>

      {/* -------------------TITLE--------------------------------------------------------------------------- */}
      <InputGroup className="mb-3">
        <Form.Control
          placeholder="Title"
                  aria-describedby="inputGroup-sizing-default"
                  value={inputTitle}
                  onChange = {(e) => setInputTitle(e.target.value)}
        />
      </InputGroup>

      {/* ------------------------DESCRIPTION------------------------------------------------------------------------------- */}
      <InputGroup className="mb-3">
        <Form.Control
          placeholder="Description"
                  aria-describedby="inputGroup-sizing-default"
                  value={inputDescription}
                  onChange = {(e) => setInputDescription(e.target.value)}
        />
      </InputGroup>

      {/* -----------------------------DATE---------------------------------------------------------------------------------------- */}
      <InputGroup className="mb-3">
        <Form.Control
          aria-label="Text input with dropdown button"
                  type="date"
                  value={inputDate}
                  onChange = {(e) => setInputDate(e.target.value)}
        />
      </InputGroup>

      <Button variant="dark" size="lg" onClick={() => addInput()}>
        Add
      </Button>

      {/* ---------------------------CARD---------------------------------------------------------------------------------------- */}

      {toDoList.map((toDo, index) => (
        <Card key={index} className="outer-card">
          <Card.Header as="h5">{toDo.name}</Card.Header>
          <Card.Body>
            <Card.Title>{toDo.title}</Card.Title>
            <Card.Text>{toDo.description}</Card.Text>
            <Card.Text>{date}</Card.Text>
            <Card.Text>{toDo.date}</Card.Text>
            <Button variant="primary">Delete</Button>
          </Card.Body>
        </Card>
      ))}
    </Container>
  );
}

export default ToDoList;
