import React from "react";
import { Container, InputGroup, Form, Dropdown, DropdownButton } from "react-bootstrap";





function ToDoList() {
    const [toDoList, setToDoList] = useState([
        {
            name: "Karthik",
            title: "ReactJS",
            description: "Learn props and state",
            date: "05-10-2022"
        }
    ])
  return (
      <Container>
          
{/* --------------NAME-------------------------------------------------------------------------- */}
      <InputGroup className="mb-3">
        <Form.Control
          placeholder="Name"
          aria-describedby="inputGroup-sizing-default"
        />
      </InputGroup>

{/* -------------------TITLE--------------------------------------------------------------------------- */}
      <InputGroup className="mb-3">
        <Form.Control
          placeholder="Title"
          aria-describedby="inputGroup-sizing-default"
        />
      </InputGroup>

{/* ------------------------DESCRIPTION------------------------------------------------------------------------------- */}
      <InputGroup className="mb-3">
        <Form.Control
          placeholder="Description"
          aria-describedby="inputGroup-sizing-default"
        />
          </InputGroup>
          

{/* -----------------------------DATE---------------------------------------------------------------------------------------- */}
<InputGroup className="mb-3">
        <Form.Control aria-label="Text input with dropdown button" type="date" />

        
      </InputGroup>     

{/* -------------------------------------------------------------------------------------------------------------------------- */}
          

    </Container>
  );
}

export default ToDoList;
